clear;
fig = gcf;
axObjs = gca;
dataObjs = axObjs.Children;
x = dataObjs(1).XData;
y = dataObjs(1).YData;
zdata1 = dataObjs(1).ZData;
cdata1 = dataObjs(1).CData;
%a = gca;
%editfigure(z,c);

%function editfigure(zdata1, cdata1)
%CREATEFIGURE(zdata1, cdata1)
%  ZDATA1:  surface zdata
%  CDATA1:  surface cdata

%  Auto-generated by MATLAB on 06-Oct-2020 12:11:16

