figure('InvertHardcopy','off','Color',[1 1 1],...
    'OuterPosition',[276 287 576 513]);
colormap(jet(64));

% Create axes
%axes1 = axes;
%hold(axes1,'on');
axes1=gca;
% Create surf
surf1 = surf(zdata1,cdata1,'FaceLighting','none','EdgeLighting','flat',...
    'FaceColor',[1 1 1],...
    'EdgeColor','interp');

